       let {showJobs} = this.state;
       return(
       <>     
                <div><button>Show</button></div>            
                <div className='job-lists'>
                        {
                            arrJobs.map((item, index) => {
                                return(
                                <div key={item.id}>
                                    {item.title} - {item.salary}
                                </div> 
                                )
                            })
                        }
                </div>
                <div><button>Hide</button></div>
        </>
        )
    }
}
